export default {
  props: {
    colors: {
      type: Array
    },
    active: {
      type: Boolean
    }
  },
  computed: {
    gradientColor () {
      const colorBottom = `color-stop(30%,  ${this.colors ? this.colors[0] : "#ff6262"})`
      const colorTop = `to(${this.colors ? this.colors[1]  : '#ffa947'})`
      return `-webkit-gradient(linear, left bottom, left top, ${colorBottom}, ${colorTop})`
    }
  }
}
